/**
 * Implementation of hook_install().
 */
function $<return module_name>_install() {
  // Install tables
  drupal_install_schema('$<return module_name>');
}

/**
 * Implementation of hook_uninstall().
 */
function $<return module_name>_uninstall() {
  // Remove tables
  drupal_uninstall_schema('$<return module_name>');

  // Remove all variable into table
  $result = db_query('SELECT name FROM {variable} WHERE name LIKE "$<return module_name>_%"');
  while ($row = db_fetch_object($result)) {
    variable_del($row->name);
  }
}

/**
 * Implementation of hook_schema().
 */
function $<return module_name>_schema() {
  $schema['$<return module_name>'] = array(
    'description' => t('${2:description}'),
    'fields' => array(
      '${3}' => array(
        'description' => t('${4}'),
        'type'        => '${5:serial}',
        'unsigned'    => ${6:TRUE},
        'not null'    => ${7:TRUE},
      ),
      '${13}' => array(
        'description' => t('${14}'),
        'type'        => '${15:int}',
        'unsigned'    => ${16:TRUE},
        'not null'    => ${17:TRUE},
        'default'     => ${18:0},
      ),
      '${23}' => array(
        'description' => t('${24}'),
        'type'        => '${25:varchar}',
        'length'      => ${26:32},
        'not null'    => ${27:TRUE},
        'default'     => '${28}',
      ),
    ),
    'primary key'     => array('${3}'),
  );
  return $schema;
}

